mapper.select:
SELECT mem_id, mem_pass, mem_name, mem_point
FROM member
WHERE mem_id = #{memId}

mapper.update:
UPDATE member
SET
    mem_name = #{memName}, 
    mem_point = #{memPoint}
WHERE mem_id = #{memId}

mapper.select:
SELECT mem_id, mem_name, mem_point
FROM member
WHERE mem_id = #{memId}
and mem_pass = #{memPass}
